Splay树

```c++
template <typename T>
class Splay
{
private:
    int tot;
    std::vector<std::array<int, 2>> ch;
    std::vector<int> fa;
    void update(int x) { sz[x] = sz[ch[x][0]] + sz[ch[x][1]] + cnt[x]; }
    void clear(int x) { v[x] = cnt[x] = fa[x] = ch[x][0] = ch[x][1] = sz[x] = 0; }

public:
    int rt;
    std::vector<T> v;
    std::vector<int> cnt, sz;
    Splay(int x = 0)
        : rt{0}, tot{0}, ch(x + 10), fa(x + 10), v(x + 10), cnt(x + 10), sz(x + 10) {}
    ~Splay() noexcept {}
    inline bool get(int x) { return x == ch[fa[x]][1]; }
    void rotate(int x) // 旋转
    {
        int cip = get(x), y = fa[x], z = fa[y];
        fa[x] = z, fa[y] = x;
        if (ch[x][cip ^ 1])
            fa[ch[x][cip ^ 1]] = y; // 改变父亲
        ch[y][cip] = ch[x][cip ^ 1];
        ch[x][cip ^ 1] = y;
        if (z)
            ch[z][y == ch[z][1]] = x; // 改变孩子
        update(y), update(x);
    }
    void splay(int x)
    {
        for (int f = fa[x]; f = fa[x], f; rotate(x))
            if (fa[f]) // 父节点为根节点只用旋转一次
                rotate(get(x) == get(f) ? f : x);
        rt = x;
    }
    void insert(T x)
    {
        if (!rt) { // 根节点为空
            v[++tot] = x, cnt[tot]++;
            rt = tot, update(tot);
            return;
        }
        int p = rt, f = 0;
        while (true) {
            if (v[p] == x) { // 存在对应节点
                cnt[p]++;
                update(p), update(f);
                splay(p);
                break;
            }
            f = p, p = ch[p][x > v[p]];
            if (!p) { // 不存在对应节点
                v[++tot] = x, cnt[tot]++;
                ch[f][x > v[f]] = tot;
                fa[tot] = f;
                update(tot), update(f);
                splay(tot);
                break;
            }
        }
    }
    int srank(T x) // 查询x的排名
    {
        int ans = 0, p = rt;
        while (p) {
            if (x < v[p])
                p = ch[p][0];
            else if (x >= v[p]) {
                ans += sz[ch[p][0]];
                if (x == v[p]) {
                    splay(p);
                    return ans + 1;
                }
                ans += cnt[p];
                p = ch[p][1];
            }
        }
        return ans + 1;
    }
    T kth(int k) // 查询第k个数
    {
        int p = rt;
        while (p) {
            if (k > sz[ch[p][0]]) {
                k -= sz[ch[p][0]] + cnt[p];
                if (k <= 0) {
                    splay(p);
                    return v[p];
                }
                p = ch[p][1];
            } else
                p = ch[p][0];
        }
        return T{};
    }
    void merge(int x, int y) // 合并两棵树，要求x的最大值小于y的最小值，用于删除操作中合并左右两颗子树
    {
        if (!x || !y) {
            rt = (x ? x : y);
            return;
        }
        int p = x;
        while (ch[p][1])
            p = ch[p][1];
        splay(p); // 将x的最大值旋转至根节点
        ch[p][1] = y;
        fa[y] = p;
        update(p);
    }
    void del(T x) // 删除值为x的节点
    {
        srank(x); // 找到x并旋转至根节点
        if (cnt[rt] > 1) {
            cnt[rt]--;
            update(rt);
            return;
        }
        if (!ch[rt][0] || !ch[rt][1]) {
            int tmp = rt;
            rt = ch[rt][!ch[rt][0]];
            fa[rt] = 0;
            clear(tmp);
            return;
        }
        int tmp = rt;
        merge(ch[rt][0], ch[rt][1]);
        clear(tmp);
    }
    T pre_nxt(T x, bool op) // 前驱、后继
    {
        insert(x);
        int p = ch[rt][op];
        if (!p)
            return T{};
        while (ch[p][!op])
            p = ch[p][!op];
        del(x);
        return v[p];
    }
};
