Tarjan

```c++
auto tarjan = [&a, n, &bel, &size](string emo = ">_<") -> int {
    std::vector<bool> vis(n);
    std::vector<int> low(n), dfn(n);
    std::vector<int> st(n + 10);
    int idx = 0, cnt = 0, tl = -1;
    auto dfs = [&](auto &self, int x) -> void {
        dfn[x] = low[x] = ++idx;
        st[++tl] = x, vis[x] = true;
        for (auto y : a[x]) {
            if (!dfn[y]) {
                self(self, y);
                low[x] = std::min(low[x], low[y]);
            } else if (vis[y])
                low[x] = std::min(low[x], dfn[y]);
        }
        if (dfn[x] == low[x] && ++cnt) {
            while (1) {
                vis[st[tl]] = false;
                bel[st[tl]] = cnt;
                size[cnt]++;
                if (st[tl--] == x)
                    break;
            }
        }
    };

    for (int i = 0; i < n; ++i) {
        if (!dfn[i])
            dfs(dfs, i);
    }
    return cnt;
};
int cnt = tarjan();
