void exgcd(ll &x, ll &y, ll a, ll b)
{
    if (b == 0) {
        x = 1, y = 0;
    } else {
        exgcd(x, y, b, a % b);
        auto t = x;
        x = y;
        y = t - a / b * y;
    }
}
