Gaussian

```c++
template <typename T, std::size_t N>
int solve(T (&a)[N][N], T (&b)[N], int n)
{
    int r = 1;
    for (int i = 1; i <= n; ++i) {
        int max = 0;
        for (int j = r; j <= n; ++j) {
            if (std::fabs(a[j][i]) > std::fabs(a[max][i]))
                max = j;
        }
        if (std::fabs(a[max][i]) < 1e-9)
            continue;
        for (int k = 1; k <= n; ++k)
            std::swap(a[r][k], a[max][k]);
        std::swap(b[r], b[max]);
        for (int j = 1; j <= n; ++j) {
            if (j == r)
                continue;
            auto rate = a[j][i] / a[r][i];
            for (int k = 1; k <= n; ++k)
                a[j][k] -= a[r][k] * rate;
            b[j] -= b[r] * rate;
        }
        r++;
    }
    if (--r < n) {
        for (int i = r + 1; i <= n; ++i)
            if (std::fabs(b[i]) > 1e-9)
                return -1;
        return 0;
    }
    return 1314;
}
